import React from 'react';

const MessageList = ({ 
  messages, 
  isGenerating, 
  serverError, 
  getConfidenceColor, 
  formatTime, 
  messagesEndRef 
}) => {
  return (
    <div className="messages-container">
      {messages.map((message) => (
        <div key={message.id} className={`message ${message.sender}`}>
          <div className="message-content">
            <div className="message-text">
              {message.sender === 'bot' && message.tokens && !message.isGenerated ? (
                <div className="token-container">
                  {message.tokens.map((token, index) => {
                    const isMask = token.char === '[MASK]';
                    const isConverting = token.isConverting; // 假设后端会提供转换状态
                    const wasOriginallyMask = token.wasOriginallyMask; // 标记原本是mask的token

                    return (
                      <span
                        key={index}
                        className={`token ${
                          token.isGenerated ? 'generated' : 'generating'
                        } ${
                          isMask ? 'mask-token' : ''
                        } ${
                          isConverting ? 'converting' : ''
                        } ${
                          wasOriginallyMask && !isMask ? 'was-mask' : ''
                        }`}
                        style={{ 
                          color: token.color,
                          animation: token.isGenerated && !isMask ? 
                            `tokenReveal 0.5s ease-in-out ${index * 0.08}s both` : 
                            (isMask && !token.isGenerated ? 'maskPulse 1.5s infinite ease-in-out' : 'none')
                        }}
                      >
                        {token.char}
                      </span>
                    );
                  })}
                </div>
              ) : (
                message.text
              )}
            </div>
            <div className="message-time">{formatTime(message.timestamp)}</div>
          </div>
        </div>
      ))}

      {isGenerating && (
        <div className="generating-indicator">
          <div className="generating-dots">
            <span></span>
            <span></span>
            <span></span>
          </div>
          <span>正在生成中...</span>
        </div>
      )}

      {serverError && (
        <div className="error-message">
          <div className="error-content">
            <strong>错误：</strong> {serverError}
          </div>
        </div>
      )}

      <div ref={messagesEndRef} />
    </div>
  );
};

export default MessageList;
