import React, { useState } from 'react';
import '../styles/Sidebar.css';

const Sidebar = ({
  conversations,
  activeConversationId,
  onNewConversation,
  onSelectConversation,
  onDeleteConversation,
  showIndicator,
  onToggleIndicator,
  systemInfo
}) => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const formatConversationTitle = (conversation) => {
    if (conversation.title) {
      return conversation.title;
    }
    // 从第一条用户消息生成标题
    const firstUserMessage = conversation.messages?.find(m => m.sender === 'user');
    if (firstUserMessage) {
      return firstUserMessage.text.slice(0, 20) + (firstUserMessage.text.length > 20 ? '...' : '');
    }
    return `对话 ${conversation.id}`;
  };

  const formatTime = (timestamp) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffInHours = (now - time) / (1000 * 60 * 60);

    if (diffInHours < 1) {
      return '刚刚';
    } else if (diffInHours < 24) {
      return `${Math.floor(diffInHours)}小时前`;
    } else if (diffInHours < 24 * 7) {
      return `${Math.floor(diffInHours / 24)}天前`;
    } else {
      return time.toLocaleDateString('zh-CN');
    }
  };

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      {/* 顶部标题区域 */}
      <div className="sidebar-header">
        <div className="system-title">
          <h2>🚀 LLaDA</h2>
          <p>扩散语言模型可视化系统</p>
        </div>
        <button 
          className="collapse-btn"
          onClick={() => setIsCollapsed(!isCollapsed)}
          title={isCollapsed ? "展开" : "收起"}
        >
          {isCollapsed ? '▶' : '◀'}
        </button>
      </div>

      {!isCollapsed && (
        <>
          {/* 控制按钮区域 */}
          <div className="sidebar-controls">
            <button 
              className="control-btn new-chat-btn"
              onClick={onNewConversation}
              title="新建对话"
            >
              <span className="btn-icon">➕</span>
              <span className="btn-text">新建对话</span>
            </button>

            <button 
              className={`control-btn indicator-btn ${showIndicator ? 'active' : ''}`}
              onClick={onToggleIndicator}
              title={showIndicator ? "隐藏置信度指示器" : "显示置信度指示器"}
            >
              <span className="btn-icon">🎨</span>
              <span className="btn-text">
                {showIndicator ? "隐藏指示器" : "显示指示器"}
              </span>
            </button>
          </div>

          {/* 对话历史列表 */}
          <div className="conversations-section">
            <h3 className="section-title">对话历史</h3>
            <div className="conversations-list">
              {conversations.length === 0 ? (
                <div className="empty-conversations">
                  <p>暂无对话记录</p>
                  <small>点击"新建对话"开始</small>
                </div>
              ) : (
                conversations.map(conversation => (
                  <div
                    key={conversation.id}
                    className={`conversation-item ${
                      activeConversationId === conversation.id ? 'active' : ''
                    }`}
                    onClick={() => onSelectConversation(conversation.id)}
                  >
                    <div className="conversation-content">
                      <div className="conversation-title">
                        {formatConversationTitle(conversation)}
                      </div>
                      <div className="conversation-meta">
                        <span className="conversation-time">
                          {formatTime(conversation.updatedAt)}
                        </span>
                        <span className="conversation-count">
                          {conversation.messages?.filter(m => m.sender === 'user').length || 0} 消息
                        </span>
                      </div>
                    </div>
                    <button
                      className="delete-conversation-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteConversation(conversation.id);
                      }}
                      title="删除对话"
                    >
                      🗑️
                    </button>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* 系统信息区域 */}
          <div className="system-info">
            <div className="info-item">
              <span className="info-label">设备：</span>
              <span className="info-value">{systemInfo?.device || 'unknown'}</span>
            </div>
            <div className="info-item">
              <span className="info-label">状态：</span>
              <span className={`status-indicator ${systemInfo?.status === 'healthy' ? 'healthy' : 'error'}`}>
                {systemInfo?.status === 'healthy' ? '🟢 正常' : '🔴 异常'}
              </span>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Sidebar;
