import React from 'react';

const MessageList = ({ 
  messages, 
  isGenerating, 
  serverError, 
  getConfidenceColor, 
  formatTime, 
  messagesEndRef 
}) => {
  return (
    <div className="messages-container">
      {messages.map((message) => (
        <div key={message.id} className={`message ${message.sender}`}>
          <div className="message-content">
            <div className="message-text">
              {message.sender === 'bot' && message.tokens ? (
                <div className="token-container">
                  {message.tokens.map((token, index) => (
                    <span
                      key={index}
                      className={`token ${token.isGenerated ? 'generated' : 'generating'}`}
                      style={{ 
                        color: token.color || getConfidenceColor(token.confidence),
                        backgroundColor: token.confidence > 0 ? 
                          `rgba(${255 * (1 - token.confidence)}, ${255 * token.confidence}, 0, 0.1)` : 
                          'transparent'
                      }}
                    >
                      {token.char}
                    </span>
                  ))}
                </div>
              ) : (
                message.text
              )}
            </div>
            <div className="message-time">{formatTime(message.timestamp)}</div>
          </div>
        </div>
      ))}

      {isGenerating && (
        <div className="generating-indicator">
          <div className="generating-dots">
            <span></span>
            <span></span>
            <span></span>
          </div>
          <span>正在生成中...</span>
        </div>
      )}

      {serverError && (
        <div className="error-message">
          <div className="error-content">
            <strong>错误：</strong> {serverError}
          </div>
        </div>
      )}

      <div ref={messagesEndRef} />
    </div>
  );
};

export default MessageList;
