import React, { useState, useEffect, useRef } from 'react';

// Token组件，处理消失动画
const TokenComponent = ({ token, index, confidenceClass, isGenerated, isGenerating, isFinalGenerated, disappearingTokens, onStartDisappearing }) => {
  const [isDisappearing, setIsDisappearing] = useState(false);
  const disappearTimeoutRef = useRef(null);

  const isMask = token.char === '[MASK]';
  const isEmpty = !token.char || token.char.trim() === '';
  const shouldDisappear = token.isGenerated && isEmpty && !isMask;
  const tokenKey = `${token.id || index}_${token.char}`;

  // 检查是否应该立即隐藏（已经在消失列表中）
  const shouldHide = disappearingTokens.has(tokenKey);

  useEffect(() => {
    if (shouldDisappear && !isDisappearing && !shouldHide) {
      console.log('Token开始消失:', token);
      setIsDisappearing(true);

      // 立即将token标记为消失中
      onStartDisappearing(tokenKey);

      // 清理之前的定时器
      if (disappearTimeoutRef.current) {
        clearTimeout(disappearTimeoutRef.current);
      }
    }
  }, [shouldDisappear, isDisappearing, shouldHide, token, tokenKey, onStartDisappearing]);

  // 如果已经被标记为消失，直接不渲染
  if (shouldHide) {
    return null;
  }

  return (
    <span
      key={tokenKey}
      className={`token ${confidenceClass} ${
        token.isGenerated && !isMask ? 'just-generated' : ''
      } ${
        !token.isGenerated ? 'generating' : ''
      } ${
        isFinalGenerated ? 'final-generated' : ''
      } ${
        isDisappearing ? 'token-disappearing' : ''
      }`}
      style={{ 
        animationDelay: token.isGenerated && !isMask ? `${index * 0.08}s` : '0s'
      }}
    >
      {token.char}
    </span>
  );
};

const MessageList = ({ 
  messages, 
  isGenerating, 
  serverError, 
  getConfidenceColor, 
  formatTime, 
  messagesEndRef 
}) => {
  // 全局跟踪所有消失中的token，使用Set存储，避免重复渲染
  const [disappearingTokens, setDisappearedTokens] = useState(new Set());

  const handleStartDisappearing = (tokenKey) => {
    setDisappearedTokens(prev => new Set([...prev, tokenKey]));
  };

  return (
    <div className="messages-container">
      {messages.map((message) => (
        <div key={message.id} className={`message ${message.sender}`}>
          <div className="message-content">
            <div className="message-text">
              {message.sender === 'bot' && message.tokens ? (
                // 优先显示token可视化，除非消息为空
                message.text && message.text.trim() === '' ? (
                  // 消息为空时，删除token格子，显示空文本
                  message.text
                ) : (
                  // 有token数据时，始终显示可视化样式
                  <div className="token-container">
                    {message.tokens.map((token, index) => {
                        const isMask = token.char === '[MASK]';

                        // 根据置信度确定CSS类名
                        let confidenceClass = '';
                        if (isMask) {
                          confidenceClass = 'mask-token';
                        } else {
                          // 根据置信度分配类名
                          if (token.confidence < 0.3) {
                            confidenceClass = 'confidence-low';
                          } else if (token.confidence < 0.7) {
                            confidenceClass = 'confidence-medium';
                          } else {
                            confidenceClass = 'confidence-high';
                          }

                          // 如果是之前生成的token（颜色为蓝色）
                          if (token.color === '#6699CC') {
                            confidenceClass = 'confidence-previous';
                          }
                        }

                        return (
                          <TokenComponent
                            key={`${message.id}_${token.id || index}_${token.char}`}
                            token={token}
                            index={index}
                            confidenceClass={confidenceClass}
                            isGenerated={token.isGenerated}
                            isGenerating={!token.isGenerated}
                            isFinalGenerated={message.isGenerated}
                            disappearingTokens={disappearingTokens}
                            onStartDisappearing={handleStartDisappearing}
                          />
                        );
                      })}
                  </div>
                )
              ) : (
                // 非bot消息或没有token数据时显示普通文本
                message.text
              )}
            </div>
            <div className="message-time">{formatTime(message.timestamp)}</div>
          </div>
        </div>
      ))}

      {isGenerating && (
        <div className="generating-indicator">
          <div className="generating-dots">
            <span></span>
            <span></span>
            <span></span>
          </div>
          <span>正在生成中...</span>
        </div>
      )}

      {serverError && (
        <div className="error-message">
          <div className="error-content">
            <strong>错误：</strong> {serverError}
          </div>
        </div>
      )}

      <div ref={messagesEndRef} />
    </div>
  );
};

export default MessageList;
