import React, { useState, useEffect, useRef, useCallback } from 'react';
import './DiffusionModel.css';

// 置信度指示器组件
const ConfidenceIndicator = ({ isVisible, onClose, position, onPositionChange }) => {
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [isClosing, setIsClosing] = useState(false);
  const indicatorRef = useRef(null);

  const confidenceLevels = [
    { level: 0, color: '#666', label: '[MASK]' },
    { level: 0.25, color: 'rgb(191, 64, 0)', label: '低置信度' },
    { level: 0.5, color: 'rgb(128, 128, 0)', label: '中置信度' },
    { level: 0.75, color: 'rgb(64, 191, 0)', label: '高置信度' },
    { level: 1.0, color: 'rgb(0, 255, 0)', label: '确定' }
  ];

  const handleMouseDown = (e) => {
    if (e.target.closest('.close-button')) return;
    
    setIsDragging(true);
    const rect = indicatorRef.current.getBoundingClientRect();
    setDragOffset({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
    e.preventDefault();
    
    // 添加全局样式防止文本选择
    document.body.style.userSelect = 'none';
    document.body.style.cursor = 'grabbing';
    document.body.classList.add('dragging-indicator');
  };

  const handleMouseMove = useCallback((e) => {
    if (!isDragging) return;
    
    const newX = e.clientX - dragOffset.x;
    const newY = e.clientY - dragOffset.y;
    
    // 限制在视窗内
    const maxX = window.innerWidth - 280;
    const maxY = window.innerHeight - 300;
    
    const constrainedX = Math.max(0, Math.min(newX, maxX));
    const constrainedY = Math.max(0, Math.min(newY, maxY));
    
    onPositionChange({
      x: constrainedX,
      y: constrainedY
    });
  }, [isDragging, dragOffset.x, dragOffset.y, onPositionChange]);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
    document.body.style.userSelect = '';
    document.body.style.cursor = '';
    document.body.classList.remove('dragging-indicator');
  }, []);

  const handleClose = () => {
    setIsClosing(true);
    // 延迟关闭以显示动画
    setTimeout(() => {
      onClose();
      setIsClosing(false);
    }, 300);
  };

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, handleMouseMove, handleMouseUp]);

  // 清理拖动状态
  useEffect(() => {
    return () => {
      document.body.style.userSelect = '';
      document.body.style.cursor = '';
    };
  }, []);

  if (!isVisible && !isClosing) return null;

  return (
    <div 
      ref={indicatorRef}
      className={`confidence-indicator ${isDragging ? 'dragging' : ''} ${isClosing ? 'closing' : ''}`}
      style={{
        left: position.x,
        top: position.y,
        cursor: isDragging ? 'grabbing' : 'grab'
      }}
      onMouseDown={handleMouseDown}
    >
      <div className="indicator-header">
        <h4>置信度指示器</h4>
        <button 
          className="close-button"
          onClick={handleClose}
          title="关闭"
        >
          ×
        </button>
      </div>
      {confidenceLevels.map((item, index) => (
        <div key={index} className="confidence-scale">
          <div 
            className="color-box" 
            style={{ backgroundColor: item.color }}
          ></div>
          <span>{item.label}</span>
        </div>
      ))}
    </div>
  );
};

const DiffusionModel = () => {
  const [messages, setMessages] = useState([
    { id: 1, text: "欢迎使用扩散语言模型可视化系统！", sender: 'bot', timestamp: new Date(), isGenerated: true }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [showIndicator, setShowIndicator] = useState(false);
  const [indicatorPosition, setIndicatorPosition] = useState({ x: 20, y: 100 });
  const messagesEndRef = useRef(null);

  // 预制的回复文本
  const predefinedResponse = "我已经接收到你的消息，这是一个基于扩散模型的语言生成系统，可以模拟从掩码到最终文本的生成过程。";

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // 根据置信度获取颜色
  const getConfidenceColor = (confidence) => {
    if (confidence === 0) return '#666'; // 灰色表示MASK
    const red = Math.floor(255 * (1 - confidence));
    const green = Math.floor(255 * confidence);
    return `rgb(${red}, ${green}, 0)`;
  };

  // 生成随机汉字
  const generateRandomChar = () => {
    const chars = '的一是了不在人有我他这个们中来上大为和国地到以说时要就出会可也你对生能而子那得于着下自之年过发后作里用道行所然家种事成方多经么去法学如都同现当没动面起看定天分还进好小部其些主样理心她本前开但因只从想实日军者意无力它与长把机十民第公此已工使情明性知全三又关点正业外将两高间由问很最重并物手应战向头文体政美相见被利什二等产或新己制身果加西斯月话合回特代内信表化老给世位次度门任常先海通教儿原东声提立及比员解水名真论处走义各入几口认条平系气题活尔更别打女变四神总何电数安少报才结反受目太量再感建务做接必场件计管期市直德资命山金指克许统区保至队形社便空决治展马科司五基眼书非则听白却界达光放强即像难且权思王象完设式色路记南品住告类求据程北边死张该交规万取拉格望觉术领共确传师观清今切院让识候带导争运笔';
    return chars[Math.floor(Math.random() * chars.length)];
  };

  // 模拟扩散过程生成单个token
  const generateToken = (targetChar, onUpdate) => {
    return new Promise((resolve) => {
      const iterations = 3 + Math.floor(Math.random() * 5); // 3-7次迭代
      let currentIteration = 0;

      const iterate = () => {
        if (currentIteration >= iterations) {
          // 最终确定的字符
          onUpdate(targetChar, 1.0);
          resolve();
          return;
        }

        const confidence = (currentIteration + 1) / iterations;
        const char = currentIteration === iterations - 1 ? targetChar : generateRandomChar();
        onUpdate(char, confidence);

        currentIteration++;
        setTimeout(iterate, 200 + Math.random() * 300); // 200-500ms随机延迟
      };

      iterate();
    });
  };

  // 生成完整的回复
  const generateResponse = async (userMessage) => {
    setIsGenerating(true);
    
    // 添加用户消息
    const userMsg = {
      id: Date.now(),
      text: userMessage,
      sender: 'user',
      timestamp: new Date(),
      isGenerated: true
    };
    
    setMessages(prev => [...prev, userMsg]);

    // 创建初始的掩码消息
    const targetChars = predefinedResponse.split('');
    const initialTokens = targetChars.map((char, index) => ({
      id: index,
      char: '[MASK]',
      confidence: 0,
      isGenerated: false
    }));

    const botMessage = {
      id: Date.now() + 1,
      text: '',
      sender: 'bot',
      timestamp: new Date(),
      tokens: initialTokens,
      isGenerated: false
    };

    setMessages(prev => [...prev, botMessage]);

    // 逐个生成token
    for (let i = 0; i < targetChars.length; i++) {
      await generateToken(targetChars[i], (char, confidence) => {
        setMessages(prev => prev.map(msg => {
          if (msg.id === botMessage.id) {
            const newTokens = [...msg.tokens];
            newTokens[i] = {
              ...newTokens[i],
              char,
              confidence,
              isGenerated: confidence === 1.0
            };
            return { ...msg, tokens: newTokens };
          }
          return msg;
        }));
      });
    }

    // 生成完成
    setMessages(prev => prev.map(msg => {
      if (msg.id === botMessage.id) {
        return { ...msg, isGenerated: true };
      }
      return msg;
    }));

    setIsGenerating(false);
  };

  const handleSend = () => {
    if (inputValue.trim() === '' || isGenerating) return;
    
    const message = inputValue.trim();
    setInputValue('');
    generateResponse(message);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey && !isGenerating) {
      e.preventDefault();
      handleSend();
    }
  };

  const formatTime = (timestamp) => {
    return timestamp.toLocaleTimeString('zh-CN', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="diffusion-model">
      <header className="app-header">
        <h1>扩散语言模型可视化系统</h1>
        <p>观察从 [MASK] 到最终文本的生成过程</p>
        <button 
          className="toggle-indicator-btn"
          onClick={() => setShowIndicator(!showIndicator)}
          title={showIndicator ? "隐藏置信度指示器" : "显示置信度指示器"}
        >
          {showIndicator ? "隐藏指示器" : "显示指示器"}
        </button>
      </header>
      
      <ConfidenceIndicator 
        isVisible={showIndicator}
        onClose={() => setShowIndicator(false)}
        position={indicatorPosition}
        onPositionChange={setIndicatorPosition}
      />
      
      <div className="chat-container">
        <div className="messages-container">
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.sender}`}>
              <div className="message-content">
                <div className="message-text">
                  {message.sender === 'bot' && message.tokens ? (
                    <div className="token-container">
                      {message.tokens.map((token, index) => (
                        <span
                          key={index}
                          className={`token ${token.isGenerated ? 'generated' : 'generating'}`}
                          style={{ 
                            color: getConfidenceColor(token.confidence),
                            backgroundColor: token.confidence > 0 ? `rgba(${255 * (1 - token.confidence)}, ${255 * token.confidence}, 0, 0.1)` : 'transparent'
                          }}
                        >
                          {token.char}
                        </span>
                      ))}
                    </div>
                  ) : (
                    message.text
                  )}
                </div>
                <div className="message-time">{formatTime(message.timestamp)}</div>
              </div>
            </div>
          ))}
          {isGenerating && (
            <div className="generating-indicator">
              <div className="generating-dots">
                <span></span>
                <span></span>
                <span></span>
              </div>
              <span>正在生成中...</span>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        
        <div className="input-container">
          <textarea
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="输入您的消息来触发扩散生成过程..."
            className="message-input"
            rows="1"
            disabled={isGenerating}
          />
          <button 
            onClick={handleSend}
            className="send-button"
            disabled={inputValue.trim() === '' || isGenerating}
          >
            {isGenerating ? '生成中...' : '发送'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default DiffusionModel;
