import React from 'react';

const SettingsPanel = ({ settings, setSettings, isGenerating }) => {
  const handleSettingChange = (key, value) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));
  };

  return (
    <div className="settings-panel">
      <div className="settings-row">
        <label>生成长度: {settings.gen_length}</label>
        <input
          type="range"
          min="16"
          max="128"
          step="8"
          value={settings.gen_length}
          onChange={(e) => handleSettingChange('gen_length', parseInt(e.target.value))}
          disabled={isGenerating}
        />
      </div>

      <div className="settings-row">
        <label>去噪步数: {settings.steps}</label>
        <input
          type="range"
          min="8"
          max="64"
          step="4"
          value={settings.steps}
          onChange={(e) => handleSettingChange('steps', parseInt(e.target.value))}
          disabled={isGenerating}
        />
      </div>

      <div className="settings-row">
        <label>温度: {settings.temperature.toFixed(1)}</label>
        <input
          type="range"
          min="0.0"
          max="1.0"
          step="0.1"
          value={settings.temperature}
          onChange={(e) => handleSettingChange('temperature', parseFloat(e.target.value))}
          disabled={isGenerating}
        />
      </div>

      <div className="settings-row">
        <label>CFG比例: {settings.cfg_scale.toFixed(1)}</label>
        <input
          type="range"
          min="0.0"
          max="2.0"
          step="0.1"
          value={settings.cfg_scale}
          onChange={(e) => handleSettingChange('cfg_scale', parseFloat(e.target.value))}
          disabled={isGenerating}
        />
      </div>
    </div>
  );
};

export default SettingsPanel;
