import axios from 'axios';

// 后端API基础URL
const API_BASE_URL = 'http://localhost:5000';

// 创建axios实例
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 60000, // 60秒超时
  headers: {
    'Content-Type': 'application/json',
  },
});

// 请求拦截器
api.interceptors.request.use(
  (config) => {
    console.log('发送请求:', config);
    return config;
  },
  (error) => {
    console.error('请求错误:', error);
    return Promise.reject(error);
  }
);

// 响应拦截器
api.interceptors.response.use(
  (response) => {
    console.log('收到响应:', response);
    return response;
  },
  (error) => {
    console.error('响应错误:', error);
    return Promise.reject(error);
  }
);

// 发送消息到后端API
export const sendMessage = async (messages, settings = {}) => {
  try {
    const response = await api.post('/generate', {
      messages,
      settings
    });
    return response.data;
  } catch (error) {
    console.error('发送消息时出错:', error);
    throw error;
  }
};

// generateText 函数 - 为了兼容性
export const generateText = sendMessage;

// 检查服务器状态
export const getStatus = async () => {
  try {
    const response = await api.get('/health');
    return response.data;
  } catch (error) {
    console.error('获取服务器状态时出错:', error);
    throw error;
  }
};

// checkServerStatus 函数 - 为了兼容性
export const checkServerStatus = getStatus;

export default api;
