import React, { useState } from 'react';
import '../styles/Sidebar.css';

const Sidebar = ({
  conversations,
  activeConversationId,
  onNewConversation,
  onSelectConversation,
  onDeleteConversation,
  systemStatus,
  isGenerating
}) => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const formatConversationTitle = (conversation) => {
    if (conversation.title) {
      return conversation.title;
    }
    // ‰ªéÁ¨¨‰∏ÄÊù°Áî®Êà∑Ê∂àÊÅØÁîüÊàêÊ†áÈ¢ò
    const firstUserMessage = conversation.messages?.find(m => m.sender === 'user');
    if (firstUserMessage) {
      return firstUserMessage.text.slice(0, 20) + (firstUserMessage.text.length > 20 ? '...' : '');
    }
    return `ÂØπËØù ${conversation.id}`;
  };

  const formatTime = (timestamp) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffMs = now.getTime() - time.getTime();
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      return time.toLocaleTimeString('zh-CN', { 
        hour: '2-digit', 
        minute: '2-digit' 
      });
    } else if (diffDays === 1) {
      return 'Êò®Â§©';
    } else if (diffDays < 7) {
      return `${diffDays}Â§©Ââç`;
    } else {
      return time.toLocaleDateString('zh-CN', { 
        month: 'short', 
        day: 'numeric' 
      });
    }
  };

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      {/* È°∂ÈÉ®Á≥ªÁªü‰ø°ÊÅØ */}
      <div className="sidebar-header">
        <div className="system-info">
          <div className="system-status">
            <div className={`status-dot ${systemInfo?.status === 'running' ? 'running' : 'stopped'}`}></div>
            <span className="status-text">{systemInfo?.model || 'LLaDA'}</span>
          </div>
          <div className="device-info">
            <span className="device-text">{systemInfo?.device || 'CPU'}</span>
          </div>
        </div>

        <button 
          className="collapse-btn"
          onClick={() => setIsCollapsed(!isCollapsed)}
          title={isCollapsed ? "Â±ïÂºÄ‰æßËæπÊ†è" : "Êî∂Ëµ∑‰æßËæπÊ†è"}
        >
          <span className="collapse-icon">{isCollapsed ? '‚ñ∂' : '‚óÄ'}</span>
        </button>
      </div>

      {!isCollapsed && (
        <div className="sidebar-content">
          {/* ÊéßÂà∂ÊåâÈíÆÂå∫Âüü */}
          <div className="controls-section">
            <button 
              className="control-btn new-conversation-btn"
              onClick={onNewConversation}
              title="Êñ∞Âª∫ÂØπËØù"
            >
              <span className="btn-icon">‚ûï</span>
              <span className="btn-text">Êñ∞Âª∫ÂØπËØù</span>
            </button>
          </div>

          {/* ÂØπËØùÂéÜÂè≤ÂàóË°® */}
          <div className="conversations-section">
            <h3 className="section-title">ÂØπËØùÂéÜÂè≤</h3>
            <div className="conversations-list">
              {conversations.length === 0 ? (
                <div className="empty-conversations">
                  <p>ÊöÇÊó†ÂØπËØùËÆ∞ÂΩï</p>
                  <p className="hint">ÁÇπÂáª"Êñ∞Âª∫ÂØπËØù"ÂºÄÂßãËÅäÂ§©</p>
                </div>
              ) : (
                conversations.map((conversation) => (
                  <div
                    key={conversation.id}
                    className={`conversation-item ${activeConversationId === conversation.id ? 'active' : ''}`}
                    onClick={() => onSelectConversation(conversation.id)}
                  >
                    <div className="conversation-content">
                      <div className="conversation-title">
                        {conversation.name || formatConversationTitle(conversation)}
                      </div>
                      <div className="conversation-preview">
                        {conversation.history?.length > 0 
                          ? conversation.history[conversation.history.length - 1]?.text?.slice(0, 30) + '...'
                          : 'Êñ∞ÂØπËØù'
                        }
                      </div>
                      <div className="conversation-time">
                        {conversation.lastUpdate 
                          ? formatTime(conversation.lastUpdate)
                          : 'ÂàöÂàö'
                        }
                      </div>
                    </div>
                    <button 
                      className="delete-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteConversation(conversation.id);
                      }}
                      title="Âà†Èô§ÂØπËØù"
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      )}

      {/* Á≥ªÁªüÁä∂ÊÄÅÂå∫Âüü */}
      {!isCollapsed && (
        <div className="system-status">
          <div className="status-title">Á≥ªÁªüÁä∂ÊÄÅ</div>

          <div className="status-item">
            <div className="status-label">ÂêéÁ´ØËøûÊé•</div>
            <div className={`status-value ${systemStatus?.backendConnected ? 'connected' : 'disconnected'}`}>
              <div className={`status-dot ${systemStatus?.backendConnected ? 'success' : 'error'}`}></div>
              {systemStatus?.backendConnected ? 'Â∑≤ËøûÊé•' : 'ËøûÊé•Â§±Ë¥•'}
            </div>
          </div>

          <div className="status-item">
            <div className="status-label">ËÆ°ÁÆóËÆæÂ§á</div>
            <div className="status-value">
              <div className={`device-icon ${systemStatus?.device?.toLowerCase()}`}>
                {systemStatus?.device === 'cuda' ? 'üéÆ' : 'üíª'}
              </div>
              {systemStatus?.device === 'cuda' ? 'GPUÂä†ÈÄü' : 'CPUÊ®°Âºè'}
            </div>
          </div>

          <div className="status-item">
            <div className="status-label">Ê®°ÂûãÁä∂ÊÄÅ</div>
            <div className={`status-value ${systemStatus?.modelLoaded ? 'loaded' : 'loading'}`}>
              <div className={`status-dot ${systemStatus?.modelLoaded ? 'success' : 'warning'}`}></div>
              {systemStatus?.modelLoaded ? 'Â∑≤Âä†ËΩΩ' : 'Âä†ËΩΩ‰∏≠'}
            </div>
          </div>

          {isGenerating && (
            <div className="status-item">
              <div className="status-label">ÁîüÊàêÁä∂ÊÄÅ</div>
              <div className="status-value generating">
                <div className="generating-spinner"></div>
                Ê≠£Âú®ÁîüÊàê...
              </div>
            </div>
          )}

          {systemStatus?.lastCheck && (
            <div className="last-check">
              ÊúÄÂêéÊ£ÄÊü•: {new Date(systemStatus.lastCheck).toLocaleTimeString()}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Sidebar;
